INSERT INTO castles (id, name, region, type, built_century, latitude, longitude, description, visiting_info, best_seasons, admission_fee, managed_by, rank) VALUES
('1', 'Edinburgh Castle', 'Edinburgh', 'Royal Castle', '12th Century', 55.9486, -3.1999, 'Scotland''s most famous castle, perched on Castle Rock overlooking the capital. Home to the Crown Jewels, Stone of Destiny, and the One O''Clock Gun.', 'Open daily with timed entry tickets. Allow 2-3 hours for full visit.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £19.50', 'Historic Environment Scotland', 1),
('2', 'Stirling Castle', 'Stirling', 'Royal Castle', '12th Century', 56.1242, -3.9456, 'One of Scotland''s grandest castles with spectacular views. Witness to many pivotal moments in Scottish history including battles and royal ceremonies.', 'Open daily. Great for families with interactive exhibits and costumed interpreters.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £16.00', 'Historic Environment Scotland', 2),
('3', 'Eilean Donan Castle', 'Highland', 'Clan Castle', '13th Century', 57.2742, -5.5164, 'Scotland''s most photographed castle, situated on a small tidal island. Featured in countless films and offering breathtaking Highland scenery.', 'Open March to October. Popular for weddings and photography. Book ahead in summer.', '{"May", "June", "July", "August", "September"}', 'Adult £10.00', 'Private', 3),
('4', 'Urquhart Castle', 'Highland', 'Historic Fortress', '13th Century', 57.3230, -4.4364, 'Dramatic ruins overlooking Loch Ness. Perfect for monster spotting! One of Scotland''s largest castles with a fascinating visitor centre.', 'Open daily with excellent visitor centre. Great views over Loch Ness.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £11.00', 'Historic Environment Scotland', 4),
('5', 'Caerlaverock Castle', 'Dumfries and Galloway', 'Historic Fortress', '13th Century', 55.0057, -3.5262, 'Unique triangular castle with impressive twin-towered gatehouse. Surrounded by a water-filled moat and set in beautiful countryside.', 'Open daily. Family-friendly with nature reserve nearby. Medieval siege machine demonstrations.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 5),
('6', 'Dunnottar Castle', 'Aberdeenshire', 'Historic Fortress', '14th Century', 56.9461, -2.1969, 'Dramatic clifftop ruins where the Scottish Crown Jewels were once hidden. Spectacular coastal location with breathtaking views.', 'Open daily. Steep walk down to castle. Stunning photography opportunities.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £7.00', 'Private', 6),
('7', 'Glamis Castle', 'Angus', 'Royal Castle', '14th Century', 56.6206, -3.0186, 'Childhood home of the Queen Mother and birthplace of Princess Margaret. Beautiful fairy-tale castle with magnificent gardens.', 'Guided tours of castle and grounds. Famous for its ghosts and royal connections.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £15.50', 'Private', 7),
('8', 'Culzean Castle', 'South Ayrshire', 'Palace', '18th Century', 55.3494, -4.7894, 'Elegant castle designed by Robert Adam, perched on dramatic cliffs. Beautiful country park with beaches and woodland walks.', 'Castle and country park. Perfect for families. Eisenhower exhibition.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £18.50', 'National Trust for Scotland', 8),
('9', 'Inveraray Castle', 'Argyll and Bute', 'Clan Castle', '18th Century', 56.2332, -5.0743, 'Fairy-tale castle and ancestral home of the Duke of Argyll. Beautiful lakeside location with stunning interiors and armory.', 'Open April to October. Famous for Downton Abbey filming. Beautiful gardens.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £13.00', 'Private', 9),
('10', 'Balmoral Castle', 'Aberdeenshire', 'Royal Castle', '19th Century', 57.0435, -3.2269, 'The Royal Family''s private residence in Scotland. Beautiful estate with gardens, exhibitions and stunning Highland scenery.', 'Open April to July only. Ballroom and grounds. Book exhibitions in advance.', '{"April", "May", "June", "July"}', 'Adult £15.00', 'Royal Family', 10),
('11', 'Craigievar Castle', 'Aberdeenshire', 'Historic Fortress', '17th Century', 57.2089, -2.6789, 'Pink fairy-tale castle that inspired Disney. Remarkable seven-story tower house with original painted ceilings.', 'Limited access - exterior viewing and some interior tours. Very popular.', '{"May", "June", "July", "August", "September"}', 'Adult £12.00', 'National Trust for Scotland', 11),
('12', 'Cawdor Castle', 'Highland', 'Clan Castle', '14th Century', 57.5055, -3.9989, 'Beautiful castle associated with Shakespeare''s Macbeth. Stunning gardens and fascinating family history.', 'Open May to October. Private family home with beautiful gardens.', '{"May", "June", "July", "August", "September", "October"}', 'Adult £12.00', 'Private', 12),
('13', 'Castle Fraser', 'Aberdeenshire', 'Historic Fortress', '16th Century', 57.2856, -2.5198, 'Spectacular Z-plan castle, one of the most elaborate tower houses in Scotland. Beautiful parkland and walled garden.', 'Open Easter to October. Family-friendly with adventure playground.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £12.50', 'National Trust for Scotland', 13),
('14', 'Floors Castle', 'Scottish Borders', 'Palace', '18th Century', 55.6067, -2.4567, 'Scotland''s largest inhabited castle, home to the Duke of Roxburghe. Magnificent art collection and beautiful gardens.', 'Open Easter to October. Family home with excellent tearoom.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £12.00', 'Private', 14),
('15', 'Tantallon Castle', 'East Lothian', 'Historic Fortress', '14th Century', 56.0572, -2.6478, 'Spectacular clifftop fortress facing the Bass Rock. Massive red sandstone walls and dramatic coastal setting.', 'Open daily. Excellent for photography and birdwatching. Windy location!', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 15),
('16', 'Doune Castle', 'Stirling', 'Historic Fortress', '14th Century', 56.1859, -4.0506, 'Well-preserved medieval castle famous for Monty Python and Outlander filming. Excellent audio guide narrated by Terry Jones.', 'Open daily. Famous film location. Interactive audio guide included.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 16),
('17', 'Kilchurn Castle', 'Argyll and Bute', 'Clan Castle', '15th Century', 56.4089, -5.0267, 'Romantic ruins on Loch Awe with stunning mountain backdrop. One of Scotland''s most photographed castle ruins.', 'Seasonal access. Free entry. Beautiful for photography. Can be reached by boat.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Free', 'Historic Environment Scotland', 17),
('18', 'Blackness Castle', 'West Lothian', 'Historic Fortress', '15th Century', 55.9825, -3.5156, 'Ship-shaped fortress on the Forth, nicknamed "the ship that never sailed". Featured in Outlander as Fort William.', 'Open daily. Unusual ship-like design. Great views over the Forth.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 18),
('19', 'Crathes Castle', 'Aberdeenshire', 'Historic Fortress', '16th Century', 57.0978, -2.5267, 'Beautiful tower house with remarkable painted ceilings and stunning gardens. Famous for its topiary and herbaceous borders.', 'Open daily. Magnificent gardens. Family-friendly with adventure playground.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £13.50', 'National Trust for Scotland', 19),
('20', 'Duart Castle', 'Isle of Mull', 'Clan Castle', '13th Century', 56.4556, -5.6578, 'Ancestral seat of Clan MacLean on dramatic clifftop overlooking the Sound of Mull. Recently restored with clan exhibitions.', 'Open May to October. Spectacular location. Rich clan history.', '{"May", "June", "July", "August", "September", "October"}', 'Adult £7.50', 'Private', 20),
('21', 'Brodick Castle', 'Isle of Arran', 'Clan Castle', '13th Century', 55.5836, -5.1508, 'Set in beautiful gardens on the Isle of Arran with stunning mountain backdrop. Former seat of the Dukes of Hamilton.', 'Open April to October. Beautiful gardens and country park. Ferry access from mainland.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £13.50', 'National Trust for Scotland', 21),
('22', 'Bothwell Castle', 'South Lanarkshire', 'Historic Fortress', '13th Century', 55.8089, -4.0656, 'One of Scotland''s finest stone castles, built by the powerful Murray family. Impressive great hall and cylindrical donjon.', 'Open daily. Excellent preservation. Rich medieval history with English and Scottish connections.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 22),
('23', 'Dirleton Castle', 'East Lothian', 'Historic Fortress', '13th Century', 56.0244, -2.7789, 'Picturesque ruined castle with beautiful gardens. Famous for its longest herbaceous border in the world.', 'Open daily. Stunning gardens. Perfect for families and photography.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £6.00', 'Historic Environment Scotland', 23),
('24', 'Drum Castle', 'Aberdeenshire', 'Historic Fortress', '13th Century', 57.1067, -2.3456, 'Ancient tower house with Victorian mansion addition. Home to the Irvine family for over 650 years.', 'Open Easter to October. Family-friendly with woodland walks and adventure playground.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £12.50', 'National Trust for Scotland', 24),
('25', 'Falkland Palace', 'Fife', 'Royal Castle', '16th Century', 56.2533, -3.2078, 'Renaissance palace and hunting lodge of the Stuart kings. Beautiful gardens and real tennis court.', 'Open March to October. Historic royal apartments. Famous for Outlander filming.', '{"March", "April", "May", "June", "July", "August", "September", "October"}', 'Adult £12.00', 'National Trust for Scotland', 25),
('26', 'Huntly Castle', 'Aberdeenshire', 'Historic Fortress', '12th Century', 57.4444, -2.7789, 'Magnificent ruined palace with elaborate heraldic decorations. Former seat of the powerful Gordon family.', 'Open daily. Excellent interpretation. Beautiful carved stonework and inscriptions.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 26),
('27', 'Kelso Abbey', 'Scottish Borders', 'Ruin', '12th Century', 55.5989, -2.4333, 'Impressive abbey ruins, once one of the largest and richest abbeys in Scotland. Beautiful border location.', 'Open daily. Free entry. Peaceful location for reflection and photography.', '{"All year"}', 'Free', 'Historic Environment Scotland', 27),
('28', 'Linlithgow Palace', 'West Lothian', 'Royal Castle', '15th Century', 55.9767, -3.5956, 'Magnificent ruined royal palace, birthplace of Mary Queen of Scots. Beautiful lakeside location.', 'Open daily. Rich royal history. Famous for TV and film locations.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £7.50', 'Historic Environment Scotland', 28),
('29', 'Neidpath Castle', 'Scottish Borders', 'Historic Fortress', '14th Century', 55.6178, -3.2456, 'Romantic tower house overlooking the River Tweed. Well-preserved example of a Scottish tower house.', 'Limited opening. Private tours available. Beautiful riverside location.', '{"July", "August"}', 'Adult £5.00', 'Private', 29),
('30', 'Scone Palace', 'Perth and Kinross', 'Palace', '19th Century', 56.4189, -3.4267, 'Historic coronation site of Scottish kings. Beautiful palace with magnificent collections and grounds.', 'Open April to October. Historic coronation stone replica. Beautiful parkland.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Adult £12.50', 'Private', 30),
('31', 'Threave Castle', 'Dumfries and Galloway', 'Historic Fortress', '14th Century', 54.9056, -3.9378, 'Island fortress accessed by small boat. Built by the fearsome Black Douglas family.', 'Seasonal boat access. Operated by Historic Environment Scotland. Weather dependent.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 31),
('32', 'Dunvegan Castle', 'Isle of Skye', 'Clan Castle', '13th Century', 57.4456, -6.5789, 'Oldest continuously inhabited castle in Scotland, seat of Clan MacLeod for over 800 years.', 'Open daily March to October. Boat trips to see seals. Beautiful gardens.', '{"March", "April", "May", "June", "July", "August", "September", "October"}', 'Adult £15.00', 'Private', 32),
('33', 'Rothesay Castle', 'Isle of Bute', 'Royal Castle', '13th Century', 55.8367, -5.0556, 'Unique circular castle on the beautiful Isle of Bute. Former royal residence with unusual design.', 'Open daily. Unique circular design. Easy ferry access from mainland.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 33),
('34', 'Castle Tioram', 'Highland', 'Clan Castle', '14th Century', 56.7889, -5.8456, 'Romantic ruined castle on a tidal island in Loch Moidart. Dramatic Highland setting.', 'External viewing only. Tidal access. Beautiful for photography. No interior access.', '{"April", "May", "June", "July", "August", "September", "October"}', 'Free', 'Private', 34),
('35', 'Dunstaffnage Castle', 'Argyll and Bute', 'Historic Fortress', '13th Century', 56.4567, -5.4389, 'Ancient fortress near Oban, former stronghold of the MacDougall clan. Strategic coastal location.', 'Open daily. Rich clan history. Beautiful coastal views and nearby chapel ruins.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 35),
('36', 'Corgarff Castle', 'Aberdeenshire', 'Historic Fortress', '16th Century', 57.2578, -3.2489, 'Remote tower house in the Cairngorms, later used as military barracks. Star-shaped defensive wall.', 'Open weekends May to September. Remote location. Military history interpretation.', '{"May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 36),
('37', 'Duffus Castle', 'Moray', 'Historic Fortress', '12th Century', 57.7089, -3.3456, 'Impressive motte and bailey castle with later stone additions. Fine example of medieval development.', 'Open daily. Free entry. Excellent example of motte and bailey development.', '{"All year"}', 'Free', 'Historic Environment Scotland', 37),
('38', 'Fast Castle', 'Scottish Borders', 'Ruin', '14th Century', 55.8789, -2.1367, 'Dramatic clifftop ruins on the Berwickshire coast. Inspiration for Wolf''s Crag in Scott''s novels.', 'Coastal walk access only. Dangerous cliffs. For experienced walkers only.', '{"April", "May", "June", "July", "August", "September"}', 'Free', 'Historic Environment Scotland', 38),
('39', 'Hermitage Castle', 'Scottish Borders', 'Historic Fortress', '14th Century', 55.2578, -2.7789, 'Grim border fortress known as the "guardhouse of the bloodiest valley in Britain". Mary Queen of Scots connection.', 'Open daily April to September. Dark history. Remote moorland location.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £5.00', 'Historic Environment Scotland', 39),
('40', 'Kisimul Castle', 'Isle of Barra', 'Clan Castle', '15th Century', 57.0089, -7.4956, 'Dramatic castle on a rock in Castlebay harbour. Seat of Clan MacNeil in the Outer Hebrides.', 'Boat access April to September. Weather dependent. Remote island location.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 40),
('41', 'Lochleven Castle', 'Perth and Kinross', 'Historic Fortress', '14th Century', 56.2056, -3.3789, 'Island castle famous as the prison of Mary Queen of Scots. Beautiful loch setting with boat access.', 'Boat access April to September. Historic royal prisoner connections. Nature reserve.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 41),
('42', 'MacLellan''s Castle', 'Dumfries and Galloway', 'Historic Fortress', '16th Century', 54.9367, -4.0456, 'Elaborate ruined mansion in Kirkcudbright. Built by a wealthy merchant family.', 'Open daily April to September. Beautiful carved stonework. Artist town location.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £5.00', 'Historic Environment Scotland', 42),
('43', 'Newark Castle', 'Renfrewshire', 'Historic Fortress', '15th Century', 55.9456, -4.7289, 'Well-preserved tower house on the Clyde with later mansion addition. Beautiful river views.', 'Open weekends April to September. Industrial heritage area. River Clyde location.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £5.00', 'Historic Environment Scotland', 43),
('44', 'Ravenscraig Castle', 'Fife', 'Historic Fortress', '15th Century', 56.1178, -3.0456, 'One of the first castles in Scotland built to withstand cannon fire. Coastal location in Kirkcaldy.', 'Open daily. Free entry. Early artillery fortification. Coastal park location.', '{"All year"}', 'Free', 'Historic Environment Scotland', 44),
('45', 'Sween Castle', 'Argyll and Bute', 'Historic Fortress', '12th Century', 55.9789, -5.6367, 'One of Scotland''s earliest stone castles. Remote location on the Knapdale peninsula.', 'Open daily. Free entry. Remote location. Ancient stone construction techniques.', '{"All year"}', 'Free', 'Historic Environment Scotland', 45),
('46', 'Tolquhon Castle', 'Aberdeenshire', 'Historic Fortress', '16th Century', 57.4767, -2.1456, 'Renaissance mansion with elaborate architectural details. Well-preserved great hall.', 'Open daily April to September. Excellent preservation. Beautiful architectural details.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £5.00', 'Historic Environment Scotland', 46),
('47', 'Wemyss Castle', 'Fife', 'Historic Fortress', '15th Century', 56.1689, -3.0567, 'Coastal castle with connections to Mary Queen of Scots. Beautiful Fife coastal location.', 'External viewing only. Private residence. Historic royal connections.', '{"All year"}', 'Free', 'Private', 47),
('48', 'Edzell Castle', 'Angus', 'Historic Fortress', '16th Century', 56.8067, -2.6789, 'Renaissance castle famous for its unique pleasance garden with symbolic sculptures.', 'Open daily April to September. Unique walled garden. Beautiful carved details.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £6.00', 'Historic Environment Scotland', 48),
('49', 'Huntingtower Castle', 'Perth and Kinross', 'Historic Fortress', '15th Century', 56.4189, -3.5067, 'Twin towers connected by later building. Scene of the Raid of Ruthven involving James VI.', 'Open daily April to September. Unique twin tower design. Royal intrigue history.', '{"April", "May", "June", "July", "August", "September"}', 'Adult £5.00', 'Historic Environment Scotland', 49),
